
$(function () {

    $('.slider').slick({
        vertical: true,
        verticalSwiping: true,
        slidesToShow: 3,
        arrows: true,
        prevArrow: $('.prev'),
        nextArrow: $('.next'),
        responsive: [
            {
                breakpoint: 500,
                settings: {
                    vertical: false,
                    slidesToShow: 1,
                    verticalSwiping: false,
                }
            }
        ]

    })

})
let body = document.querySelector('body')
let modal = document.querySelector('.modal')
const sliderItem = document.querySelectorAll('.slider__item')
const menuItem = document.querySelectorAll('.menu__item')
const menuItemMobile = document.querySelectorAll('.menu__item-mobile')
const aboutMeClick = document.querySelector('.about__me-click')


aboutMeClick.addEventListener('click', () => {
    const aboutMe = document.querySelector('.about__me')
    $('.main__section').addClass('opacity')
    aboutMe.classList.add('active')
    body.classList.add('fixed')
})


