let removeAllAcvtive = () => {
    $('.modal').removeClass('active')
    $('.menu').removeClass('active')
    $('body').removeClass('fixed')
    $('.main__section').removeClass('opacity')
    $('header').removeClass('opacity')
    $('.about__me').removeClass('active')
}





sliderItem.forEach(function (data, counter) {
    data.addEventListener('click', function () {
        modal.innerHTML = modalData[counter] + '<div  class="close">Закрыть</div>'
        modal.classList.add('active')
        $('.main__section').addClass('opacity')
        const closeButton = document.querySelector('.close')
        closeButton.addEventListener('click', clickClose)
        body.classList.add('fixed')
        $('.menu').removeClass('active')
    })
})


menuItem.forEach(function (data, counter) {
    data.addEventListener('click', function () {
        console.log(counter)
        modal.innerHTML = modalData[counter] + '<div  class="close">Закрыть</div>'
        modal.classList.add('active')
        $('.main__section').addClass('opacity')
        const closeButton = document.querySelector('.close')
        closeButton.addEventListener('click', clickClose)
        body.classList.add('fixed')
        $('.menu').removeClass('active')
        
    })
})


menuItemMobile.forEach(function (data, counter) {
    data.addEventListener('click', function () {
        console.log(counter)
        modal.innerHTML = modalData[counter] + '<div  class="close">Закрыть</div>'
        modal.classList.add('active')
        $('.main__section').addClass('opacity')
        const closeButton = document.querySelector('.close')
        closeButton.addEventListener('click', clickClose)
        body.classList.add('fixed')
        $('.menu').removeClass('active')
        $('.mobile__poems-menu').removeClass('active')
        
    })
})

let clickClose = () => {
    removeAllAcvtive()
}


$('.close__button').click(function () {
    removeAllAcvtive()

})


$('.my__poems').click(function () {
    $('.menu').addClass('active')
    $('body').addClass('fixed')
    $('.main__section').addClass('opacity')
    $('header').addClass('opacity')
})



$('.close__menu').click(function () {
    removeAllAcvtive()
})



$('.contacts__link').click(function () {
    $('.contacts').addClass('active')
    $('header').addClass('opacity')
    $('body').addClass('fixed')
    $('.main__section').addClass('opacity')
})


$('.close__contacts').click(function () {
    $('.contacts').removeClass('active')
    removeAllAcvtive()
})


$('.header__burger-inner').click(function(){
    $('.header__burger-inner').toggleClass('active')
    $('.main__section').toggleClass('opacity')
    $('.mobile__menu').toggleClass('active')
})


$('.about__me-title').click(function(){
    $('.dropdown-about').toggleClass('active')
})


$('.myPoems').click(function(){
    $('.mobile__poems-menu').addClass('active')
    $('.header__burger-inner').toggleClass('active')
    $('.main__section').toggleClass('opacity')
    $('.mobile__menu').toggleClass('active')
})



$('.close-mobile').click(function(){
    $('.mobile__poems-menu').removeClass('active')
})


